-- Дамп структуры для таблица aii.blog_categories
DROP TABLE IF EXISTS `blog_categories`;
CREATE TABLE IF NOT EXISTS `blog_categories` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'Id',
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Наименование',
  `alias` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Алиас',
  `text` text COLLATE utf8_unicode_ci COMMENT 'Описание',
  `date` date DEFAULT NULL COMMENT 'Дата публикации',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Опубликовано',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique` (`alias`),
  KEY `key` (`alias`,`status`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='Категории';

-- Дамп данных таблицы aii.blog_categories: ~6 rows (приблизительно)
/*!40000 ALTER TABLE `blog_categories` DISABLE KEYS */;
INSERT INTO `blog_categories` (`id`, `name`, `alias`, `text`, `date`, `status`) VALUES
	(1, 'Политика', 'politics', 'Поли́тика (др.-греч. πολιτική «государственная деятельность») — понятие, включающее в себя деятельность органов государственной власти и государственного управления, а также вопросы и события общественной жизни, связанные с функционированием государства. Научное изучение политики ведётся в рамках политологии', '2017-02-22', 1),
	(2, 'Юмор', 'yumor', 'Ю́мор — интеллектуальная способность подмечать в явлениях их комичные, смешные стороны. Чувство юмора связано с умением субъекта обнаруживать противоречия в окружающем мире[1].\r\n\r\nВ широком смысле — всё, что может вызвать смех, улыбку и радость.', '2017-03-22', 1),
	(3, 'Культура', 'kultura', '<p><strong>Культу́ра</strong> (от лат.&nbsp;cultura&nbsp;&mdash; возделывание, позднее&nbsp;&mdash; воспитание, образование, развитие, почитание)&nbsp;&mdash; понятие, имеющее огромное количество значений в различных областях человеческой жизнедеятельности. Культура является предметом изучения философии, культурологии, истории, искусствознания, лингвистики (этнолингвистики), политологии, этнологии, психологии, экономики, педагогики&nbsp;и др.</p>\r\n<p>В основном, под культурой понимают человеческую деятельность в её самых разных проявлениях, включая все формы и способы человеческого самовыражения и самопознания, накопление человеком и социумом в целом навыков и умений. Культура предстает также проявлением человеческой субъективности и объективности (характера, компетентностей, навыков, умений и знаний).</p>\r\n<p>Культура представляет собой совокупность устойчивых форм человеческой деятельности, без которых она не может воспроизводиться, а значит&nbsp;&mdash; существовать.</p>\r\n<p>Культура&nbsp;&mdash; это набор кодов, которые предписывают человеку определённое поведение с присущими ему переживаниями и мыслями, оказывая на него, тем самым, управленческое воздействие.</p>\r\n<p>Источником происхождения культуры мыслится человеческая деятельность, познание и творчество.</p>', '2017-03-28', 1),
	(4, 'Спорт', 'sport', 'Спорт (англ. sport, сокращение от первоначального старофранц. desport — «игра», «развлечение») — организованная по определённым правилам деятельность людей, состоящая в сопоставлении их физических и/или интеллектуальных способностей, а также подготовка к этой деятельности и межличностные отношения, возникающие в её процессе.', '2017-03-28', 1),
	(5, 'Наука', 'nauka', '<p><strong>Нау́ка</strong>&nbsp;&mdash; область человеческой деятельности, направленная на выработку и систематизацию объективных знаний о действительности. Основой этой деятельности является сбор фактов, их постоянное обновление и систематизация, критический анализ и, на этой основе, синтез новых знаний или обобщений, которые не только описывают наблюдаемые природные или общественные явления, но и позволяют построить причинно-следственные связи с конечной целью прогнозирования. Те гипотезы, которые подтверждаются фактами или опытами, формулируются в виде законов природы или общества.</p>\r\n<p>Наука в широком смысле включает в себя все условия и компоненты соответствующей деятельности:</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<ul>\r\n<li>разделение и кооперацию научного труда;</li>\r\n<li>научные учреждения, экспериментальное и лабораторное оборудование;</li>\r\n<li>методы научно-исследовательской работы;</li>\r\n<li>понятийный и категориальный аппарат;</li>\r\n<li>систему научной информации;</li>\r\n<li>всю сумму накопленных ранее научных знаний.</li>\r\n</ul>\r\n<p>Науковедение &mdash; наука, изучающая науку.</p>\r\n</td>\r\n<td>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="../../../../files/uploads/nauka.png" width="100" height="114" /></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '2017-03-28', 1),
	(6, 'Test', 'test', '<h2 style="text-align: center;">Test category</h2>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="../../../../files/uploads/yii2.jpg" alt="" width="400" height="267" /></p>\r\n<p>&nbsp;</p>', '2017-03-29', 1);
/*!40000 ALTER TABLE `blog_categories` ENABLE KEYS */;

-- Дамп структуры для таблица aii.blog_comments
DROP TABLE IF EXISTS `blog_comments`;
CREATE TABLE IF NOT EXISTS `blog_comments` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'Id',
  `post_id` int(10) NOT NULL COMMENT 'Пост',
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Имя',
  `email` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'E-Mail',
  `text` text COLLATE utf8_unicode_ci COMMENT 'Текст',
  `date` date DEFAULT NULL COMMENT 'Дата',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Опубликовано',
  PRIMARY KEY (`id`),
  KEY `FK_blog_comments_blog_posts` (`post_id`),
  KEY `key` (`status`),
  CONSTRAINT `FK_blog_comments_blog_posts` FOREIGN KEY (`post_id`) REFERENCES `blog_posts` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='Комментарии к постам';

-- Дамп данных таблицы aii.blog_comments: ~15 rows (приблизительно)
/*!40000 ALTER TABLE `blog_comments` DISABLE KEYS */;
INSERT INTO `blog_comments` (`id`, `post_id`, `name`, `email`, `text`, `date`, `status`) VALUES
	(1, 1, 'Культурист', 'culturist@mail.ru', 'Пост-культура – это подобие Культуры!', '2017-03-28', 1),
	(2, 2, 'Марк Туллий Цицерон', 'mark@tuliy.cc', 'Римляне употребляли слово «культура» с каким-нибудь объектом в родительном падеже, то есть только в словосочетаниях, означающих совершенствование, улучшение того, с чем сочеталось: «culture juries» — выработка правил поведения, «culture lingual» — соверше', '2017-03-29', 1),
	(3, 4, 'Умник', 'umnik@ukr.net', 'В конце XX века внимание к культуре взаимодействия между человеком и природой существенно усилилось; причиной такого внимания стало в первую очередь общественное переосмысление подхода к культуре как таковой и к прошлым достижениям человечества в частности. Внутренний потенциал этих достижений с точки зрения их возможной реактивации в форме сохранения или восстановления традиций был существенным образом переоценён, а сами эти достижения стали рассматриваться как нечто весьма ценное: как осязаемый результат человеческой самореализации, с одной стороны, и, с другой, как продолжающий действовать фактор творческого развития человечества', '2017-03-29', 1),
	(4, 5, 'Умник', 'umnik@ukr.net', 'Шнобелевские премии, Игнобелевские премии, Антинобелевские премии (англ. Ig Nobel Prize, от игры слов: англ. ignoble — «постыдный») — пародия на престижную международную награду — Нобелевскую премию. ', '2017-03-29', 1),
	(5, 7, 'Ученый', 'professor@mit.us', 'Учёный — представитель науки, осуществляющий осмысленную деятельность по формированию научной картины мира, чья научная деятельность и квалификация в той или иной форме получили признание со стороны научного сообщества. ', '2017-03-29', 1),
	(6, 7, 'Умник', 'umnik@ukr.net', 'Среди учёных принято любую достаточно длительную работу по исследованию какой-то определённой темы завершать публикацией соответствующей монографии, которая обычно содержит детальное описание методики исследования, изложение результатов проведённой работы, а также их интерпретацию.', '2017-03-29', 1),
	(7, 7, 'Доцент', 'doc@ukr.net', 'В учёном сообществе высоко ценится педагогическая работа. Право читать лекции в престижном учебном заведении является признанием уровня и квалификации учёного. Высоко также ценится создание научной школы, то есть подготовка нескольких учёных, развивающих идеи учителя.', '2017-03-29', 1),
	(8, 9, 'Аристотель', 'aristotel@gmail.com', 'На сегодняшний день известно 20 политических и идеологических систем', '2017-03-29', 1),
	(9, 11, 'Спортсмен', 'sport@sportmaster.ru', 'В древней Персии существовала конная игра чаутам, напоминающая поло. Этой игре, как и шахматам, стрельбе, метанию копья, борьбе и бегу мальчиков обучали в специальных воспитательных домах при дворе', '2017-03-29', 1),
	(10, 12, 'Полковник', 'polkovnik@mail.ru', 'Военно-прикладные и служебно-прикладные виды спорта — виды спорта, основой которых являются специальные действия (в том числе приёмы), связанные с выполнением военнослужащими и сотрудниками некоторых федеральных органов исполнительной власти … своих служебных обязанностей, и которые развиваются в рамках деятельности одного или нескольких федеральных органов исполнительной власти.', '2017-03-29', 1),
	(11, 15, 'Петросян', 'petrosyan@mail.ru', 'Существуют различные формы юмора: ирония, пародия, сатира, анекдот, шутка, каламбур и так далее. Особый вид юмора представляет собой чёрный юмор.\r\nНаряду с текстовой формой, юмор может быть также представлен в графическом виде — карикатура, шарж и другие.\r\n\r\n', '2017-03-29', 1),
	(12, 15, 'Юморист', 'humor@ukr.net', '«Столицы» юмора:\r\nУкраина: Одесса\r\nПольша: Вонхоцк[10]\r\nБолгария: Габрово\r\nШотландия: Абердин\r\nАзербайджан: Шеки', '2017-03-29', 1),
	(13, 16, 'Умник', 'umnik@ukr.net', 'Рели́гия (лат. religare — связывать, соединять) — определённая система взглядов, обусловленная верой в сверхъестественное, включающая в себя свод моральных норм и типов поведения, обрядов, культовых действий и объединение людей в организации (церковь, умма, сангха, религиозная община)', '2017-03-29', 1),
	(14, 1, 'Иван Иваныч', 'ii@mtu.edu.ua', 'Как показывают исследователи, техногенная цивилизация, пришедшая с XVI-XVII в. на смену традиционным типам цивилизации, узаконила в качестве главных ценностных установок ориентацию на научное «рациональное» изучение мира с целью его преобразования в утилитарно-потребительском модусе, познание законов природы для подчинения ее человеческим прихотям, стимулировала научно-техническую творческую активность личности для управления законами природы, поставила «научную рациональность» выше других форм знания. ', '2017-03-30', 1),
	(15, 17, 'Test user', 'user@test.tst', 'Test comment!', '2017-03-30', 1);
/*!40000 ALTER TABLE `blog_comments` ENABLE KEYS */;

-- Дамп структуры для таблица aii.blog_posts
DROP TABLE IF EXISTS `blog_posts`;
CREATE TABLE IF NOT EXISTS `blog_posts` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'Id',
  `category_id` int(10) NOT NULL COMMENT 'Категория',
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Наименование',
  `alias` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Алиас',
  `text` text COLLATE utf8_unicode_ci COMMENT 'Текст',
  `tags` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Теги',
  `date` date DEFAULT NULL COMMENT 'Дата публикации',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Опубликовано',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique` (`alias`),
  KEY `FK_blog_posts_blog_categories` (`category_id`),
  KEY `key` (`alias`,`status`),
  FULLTEXT KEY `tags` (`tags`),
  CONSTRAINT `FK_blog_posts_blog_categories` FOREIGN KEY (`category_id`) REFERENCES `blog_categories` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='Посты';

-- Дамп данных таблицы aii.blog_posts: ~17 rows (приблизительно)
/*!40000 ALTER TABLE `blog_posts` DISABLE KEYS */;
INSERT INTO `blog_posts` (`id`, `category_id`, `name`, `alias`, `text`, `tags`, `date`, `status`) VALUES
	(1, 3, 'Пост-культура', 'post-kultura', '<p><em>Пост-культура</em> &ndash; это то <em>подобие</em> Культуры, вырастающее из нее и пока маскирующееся под нее, которое интенсивно вытесняет ее в современной цивилизации и отличается от Культуры своей сущностью. Точнее отсутствием таковой. <em>Пост</em> -культура &ndash; это будто-культурная деятельность (включая ее результаты) поколения людей, сознательно отказавшихся от <em>Великого Другого</em> как трансцендентного центра Универсума и стремящихся строить нечто в культурно-цивилизационных полях с ориентацией только и исключительно на свой разум, материалистическое миропонимание и безграничность сциентистски-технократических перспектив. С позиции Культуры <em>пост</em> -культура предстает &laquo;культурой&raquo; с <em>пустым центром,</em> как бы одной оболочкой культуры, под которой &ndash; <em>пустота.</em> Используя язык новейшей науки синергетики, можно сказать, что <em>пост-</em> культура &ndash; это та &laquo;нелинейная среда&raquo; культуры, возникшая в момент глобальной цивилизационной бифуркации, в которой &laquo;варится&raquo; бесчисленное множество возможных структур будущего становления и которая с позиции любой уже ставшей структуры представляется неким уплотненным потенциальным хаосом, или полем бесконечных возможностей.</p>\r\n<p><em>Пост</em> -культура сегодня свидетельствует с равными основаниями о двух возможностях дальнейшего развития цивилизационного процесса: или о качественном скачке на принципиально новый уровень развития человечества, сознания, нравственности, соответственно &ndash; на какой-то принципиально новый уровень Культуры, или о глобальной катастрофе и цивилизации, и человечества в целом, вплоть до самоуничтожения на путях неконтролируемой глубинными нравственными принципами (их, кстати, как мы увидим, полностью отрицает нонклассика) гонки научно-технического экспериментаторства, которое уже сегодня поставило человечество на опасную грань уничтожения.</p>', 'культура духовность', '2017-03-29', 1),
	(2, 3, 'Различные определения культуры', 'razlichnye-opredeleniya-kultury', '<p>Многообразие существующих в мире философских и научных определений&nbsp;культуры не позволяет сослаться на это понятие как на наиболее очевидное обозначение объекта и предмета культуры и требует более четкой и узкой его конкретизации: Культура понимается как&hellip;</p>\r\n<ul>\r\n<li>&laquo;Культура есть практическая реализация общечеловеческих и духовных ценностей&raquo;</li>\r\n<li>&laquo;Исторически определённый уровень развития общества и человека, выраженный в типах и формах организации жизни и деятельности людей, а также в создаваемых ими материальных и духовных ценностях&raquo; (БСЭ);</li>\r\n<li>&laquo;Совокупность генетически&nbsp;ненаследуемой информации в области поведения человека&raquo; (Ю.&nbsp;М.&nbsp;Лотман);</li>\r\n<li>&laquo;Вся совокупность небиологических проявлений человека&raquo;;</li>\r\n<li>&laquo;Культуру, в том числе наиболее блистательные и впечатляющие её проявления в виде ритуальных и религиозных служб, можно интерпретировать как иерархическую систему приспособлений и устройств для отслеживания параметров среды.&raquo; (Э.&nbsp;О.&nbsp;Уилсон);</li>\r\n</ul>', 'культура история философия', '2017-03-29', 1),
	(3, 3, 'Периодизация истории культуры', 'periodizacziya-istorii-kultury', '<p>В современной культурологии принята следующая периодизация истории европейской культуры:</p>\r\n<ul>\r\n<li>Первобытная культура (до 4 тыс. до&nbsp;н.&nbsp;э.);</li>\r\n<li>Культура Древнего мира&nbsp;(4 тыс. до&nbsp;н.&nbsp;э.&nbsp;&mdash; V&nbsp;в. н.&nbsp;э.), в которой выделяют культуру Древнего Востока&nbsp;и культуру&nbsp;Античности;</li>\r\n<li>Культура Средних веков&nbsp;(V&mdash;XIV&nbsp;вв.);</li>\r\n<li>Культура Возрождения или Ренессанса&nbsp;(XIV&mdash;XVI&nbsp;вв.);</li>\r\n<li>Культура Нового Времени (к. XVI&mdash;XIX&nbsp;вв.);</li>\r\n</ul>\r\n<p>Главной особенностью периодизации истории культуры является выделение культуры эпохи Возрождения как самостоятельного периода развития культуры, в то время как в исторической науке данная эпоха считается поздним Средневековьем или ранним Новым временем.</p>', '', '2017-03-29', 1),
	(4, 3, 'Культура и природа', 'kultura-i-priroda', '<p>Взаимодействие культуры и природы&nbsp;&mdash; это одна из проблем культурологии.</p>\r\n<p>Марк Порций Катон Старший&nbsp;ввёл понятие культуры, определяя его как возделывание, улучшение, почитание, относя его к обработке земли. В таком понимании культура предстаёт как сотрудничество человека с природой, направленное на взаимную пользу. При таком сотрудничестве человек принимает от природы её материальные составляющие, черпает замыслы, учится, в ответ обогащая природу творчеством своего труда, одухотворяя её. Действия же направленные на разрушение природы не могут не навредить и человечеству в ней живущему. Такие действия не могут являться результатом высшей сознательной деятельности, проявлением которой и является культура.</p>\r\n<p>Современное, не редко встречающееся противопоставление культуры и природы возможно при допущении мысли об отдельности человеческого существования от природы и отождествления культуры и городской жизни, цивилизации.&nbsp;Нетрудно найти примеры проявлений, относящихся к городской жизни которые не могут быть отнесены к области культуры и даже стать примером упадка культуры и её отсутствия. Подобные мнения можно найти в работах И. Г. Гердера, Н. А. Бердяева.</p>\r\n<p>Нетрудно убедиться, что отстранение человека от принципов разумного сотрудничества с природой, его порождающей, приводит к упадку накопленного культурного достояния, а после и к упадку самой цивилизованной жизни. Примером тому могут служить упадок многих развитых государств древнего мира и многочисленные проявления кризиса культуры в жизни современных мегаполисов.</p>', 'культура природа история', '2017-03-29', 1),
	(5, 5, 'Медали и премии', 'medali-i-premii', '<p>За научные достижения учёным присуждаются научные премии и медали.</p>\r\n<ul>\r\n<li>Нобелевская премия &mdash; самая престижная и знаменитая научная премия, присуждается в ряде номинаций. На неё существует пародия в виде Шнобелевской премии.</li>\r\n<li title="Нобелевская премия">Премия и медаль Филдса &mdash; за успехи в области математики.</li>\r\n<li title="Нобелевская премия">Премия Неванлинны &mdash; за крупные достижения в математических аспектах информатики.</li>\r\n<li title="Нобелевская премия">Премия Гаусса &mdash; за выдающийся вклад в математику посредством открытий в других науках.</li>\r\n<li title="Нобелевская премия">Премия Крафорда &mdash; награда вручается по следующим направлениям: Астрономия и Математика, Биологические науки и Науки о Земле.</li>\r\n<li title="Нобелевская премия">Премия Абеля &mdash; за вклад в математику.</li>\r\n<li title="Нобелевская премия">Премия Шао &mdash; за вклад в астрономию, математику и медицину или науки о жизни.</li>\r\n<li title="Нобелевская премия">Премия Тьюринга &mdash; самая престижная премия в информатике, вручаемая Ассоциацией вычислительной техники.</li>\r\n<li title="Нобелевская премия">Премия Декарта &mdash; за выдающиеся достижения в науке и технике.</li>\r\n<li title="Нобелевская премия">Большая золотая медаль имени М. В. Ломоносова &mdash; высшая награда Российской академии наук.</li>\r\n<li title="Нобелевская премия">Золотая медаль имени Д. И. Менделеева &mdash; научная награда Российской академии наук за выдающиеся научные работы в области химической науки и технологии.</li>\r\n</ul>', 'наука', '2017-03-29', 1),
	(6, 5, 'История науки', 'istoriya-nauki', '<p>С развитием письменности в странах древних цивилизаций накапливались и осмысливались эмпирические знания о природе, человеке и обществе, возникали зачатки математики, логики, геометрии, астрономии, медицины. Предшественниками современных учёных были философы Древней Греции и Рима, для которых размышления и поиск истины становятся основным занятием. В Древней Греции появляются варианты классификации знаний.</p>\r\n<p>Наука в современном понимании начала складываться с XVI&mdash;XVII веков. В ходе исторического развития её влияние вышло за рамки развития техники и технологии. Наука превратилась в важнейший социальный, гуманитарный институт, оказывающий значительное влияние на все сферы общества и культуру. Объём научной деятельности с XVII века удваивается примерно каждые 10&mdash;15 лет (рост открытий, научной информации, числа научных работников).</p>\r\n<p>В развитии науки чередуются экстенсивные и революционные периоды&nbsp;&mdash; научные революции, приводящие к изменению её структуры, принципов познания, категорий и методов, а также форм её организации. Для науки характерно диалектическое сочетание процессов её дифференциации и интеграции, развития фундаментальных и прикладных исследований.</p>', 'наука история', '2017-03-29', 1),
	(7, 5, 'Научное сообщество', 'nauchnoe-soobshhestvo', '<p>Совокупность занимающихся наукой людей составляет научное сообщество. Научное сообщество представляет собой сложную самоорганизующуюся систему, в которой действуют и государственные учреждения, и общественные организации, и неформальные группы. Отличительной чертой этого сообщества является повышенная степень признания авторитета, достигнутого научными успехами, и сниженный уровень признания авторитета властного, что порой приводит к конфликту государства и научного сообщества. Также следует отметить более высокую, чем в других социальных сферах, эффективность неформальных групп и особенно отдельных личностей. Важнейшими задачами научного сообщества являются признание или отрицание новых идей и теорий, обеспечивающее развитие научного знания, а также поддержка системы образования и подготовки новых научных работников.</p>\r\n<p>Образ жизни и мировоззрение людей научного сообщества могут существенно отличаться от распространённых в обществе. Считается, что сейчас в научном сообществе преобладают атеистические и скептические воззрения. Проведённые в 1990-х годах исследования показали, что лишь 7&nbsp;% членов американской Национальной академии наук и 3,3&nbsp;% членов академии наук Великобритании оказались верующими. В то же время, согласно общенациональному опросу, верующими считают себя 68,5&nbsp;% населения Великобритании. Если брать американских учёных в целом, то количество верующих составляет около 40&nbsp;% и почти не меняется со временем. Среди преподавателей американских университетов доля верующих составляет уже до 73&nbsp;%. По данным, опубликованным в июне 2005 года исследователями из университета Чикаго, 76 % американских докторов считают себя верующими. История науки свидетельствует об изменчивости господствующих представлений и доктрин в науке, а также об их зависимости от политической конъюнктуры соответствующего государства или исторического периода.</p>', 'наука сообщество', '2017-03-29', 1),
	(8, 1, 'Происхождение политики', 'proisxozhdenie-politiki', 'В античности полисы (греч. πόλις) исторически формировались как городские общины с самоуправлением, которые конституировали себя в качестве политической формации, общности — эта форма самоорганизации общества была типична для Древней Греции. Получила развитие и распространение через Италию и непосредственно через Римскую империю. С ростом государств и империи политика взаимоотношений с обширными территориями требовала изменчивости политики и совершенствования системы управления. Политика, как методология управления формировалась в полисах, где сосредотачивалась управленческая элита и различные сословия (ремёсла, искусства, школы) в которых формировалась будущая элита.\r\n\r\nСам по себе рассматриваемый термин был введён в IV веке до н. э. Аристотелем, который предлагал для него следующее определение: политика — это искусство управления государством (полисом). Впрочем, политика выделилась в качестве отдельной области социального бытия задолго до этого события — хотя и позднее, нежели, например, экономические отношения или мораль. Существует несколько различных представлений о природе и происхождении политики:\r\n\r\nТеологическое. В соответствии с данным представлением политика, равно как и жизнь в целом, имеет божественное происхождение.\r\nАнтропологическое. Такой подход увязывает политику с человеческой природой: предполагается, что соответствующий род общения и взаимодействия с другими людьми продиктован сущностью человека (и, с другой стороны, сам влияет на эту сущность, обусловливая ряд самоограничений и других характерных черт, отличающих человека от животного).\r\nБиологическое. Подобная трактовка, напротив, подразумевает, что природа политики должна пониматься на базе общих для человека и животного начал — таких, к примеру, как агрессивность, инстинкт самосохранения, борьба за выживание и т. п. Этолог К. Лоренц, в частности, увязывал с феноменом агрессивности войны, революции и другие конфликты, имеющие место в жизни общества.\r\nПсихологическое. Согласно этому представлению первоисточником политического взаимодействия между людьми являются потребности, интересы, эмоции и другие проявления человеческой психики. В традиционном для себя ключе политику трактовал, к примеру, З. Фрейд, ассоциировавший природу политики с бессознательным.\r\nСоциальное. Соответствующий подход предполагает, что политика является порождением общества и сформировалась в ходе эволюции последнего — по мере роста его сложности и развития социальной стратификации. В качестве отправной точки данных общественных изменений может рассматриваться неолитическая революция, которая повлияла как на формы хозяйствования, так и на образ жизни людей в целом. Логика появления политики при этом имеет примерно следующий вид:\r\nРост продуктивности человеческой деятельности обусловливает появление частной собственности. Последняя, в свою очередь, способствует развитию экономики, её специализации, а также формированию новых социальных объединений, углубляет автономность и независимость личности, обеспечивая ей возможность достичь определённого положения в обществе экономическим путём, а также усиливает расслоение общества по имущественному признаку, порождая конфликты.\r\nСоциальная дифференциация, в том числе по этническому и религиозному признаку, становится более выраженной.\r\nДемографический рост и расширение экономической деятельности актуализируют проблему независимости того или иного сообщества от других, а также задачу сохранения целостности территорий, находящихся под контролем указанного сообщества.\r\nСоответственно, политика возникает в связи с утратой возможности разрешить изложенные выше проблемы и конфликтные ситуации традиционными методами — посредством обычаев, моральных установок и т. п. Наравне с правом политика выступает в роли одного из новых регуляторов, предназначенных для решения этих проблем; кроме того, с той же целью формируется и государство как новая форма структуризации и организации жизни людей. В силу этого понятие политики непосредственным образом связывается с понятиями государства и власти. В концепции политолога М. Дюверже выделяются три формы власти — анонимная, индивидуализированная и институционализированная; первые две определяются как догосударственные, а третья — как собственно государственная, имеющая публичный характер и обусловливающая появление политики.', 'политика история', '2017-03-29', 1),
	(9, 1, 'Сущность политики', 'sushhnost-politiki', 'В ходе развития научной и философской мысли предлагались различные определения политики: общее «царское искусство», заключающееся во владении совокупностью конкретных (ораторским, военным, судебным и т. д.), умение «оберечь всех граждан и по возможности сделать их из худших лучшими» (Платон), знание о правильном и мудром правлении (Макиавелли), лидерство государственного аппарата или влияние на это лидерство (Макс Вебер), борьба классовых интересов (Карл Маркс). В настоящее время распространённым является толкование политики как деятельности, которая выражается в поведении общественных групп, равно как и совокупности моделей поведения и социальных институтов, управляющих общественными отношениями и создающих властный контроль как таковой вкупе с соревнованием за обладание силой власти. Существует также представление, что в самом общем виде политика может быть определена как социальная деятельность, направленная на сохранение или изменение существующего порядка распределения власти и собственности в государственно-организованном обществе (внутренняя политика) и мировом сообществе (внешняя политика, глобальная или мировая политика)[2].\r\n\r\nПолитика представляет собой многоплановое социальное явление, которое можно рассматривать как инструмент сознательного саморегулирования общества. Существует целый ряд определений политики, предлагаемых различными теоретическими направлениями, в которых подчёркивается один из основных аспектов политической деятельности: институциональный, правовой, экономический, психологический, социальный, антропологический и т. д.', 'политика философия', '2017-03-29', 1),
	(10, 1, 'Альтернативные определения', 'alternativnye-opredeleniya', 'Политика — борьба множества интересов (искусство управления с учётом интересов всех слоёв общества). Определение связано с этимологией греч. πολιτικός, где πολι (поли) означает множество, а τικός (тикос) — интерес; (дословно — «множество интересов»)[источник не указан 1907 дней]. Так, государственные служащие в городах древней Греции именовались политикос, а граждане, которые мало интересовались и участвовали в политической жизни своего города, именовались ιδιοτικός (идиотикос)[3];\r\nПолитика — искусство допустимого. История указывает на манипуляции и агрессивность политики многих правителей. Политика — управление, инструмент, и его нужно отличать от целей и фальсифицирования (имитационный характер) политики;\r\nПолитика — это всеохватывающий феномен общественной жизни, пронизывающий все её формы и включающий в себя все формы социальной активности людей, все виды деятельности по их организации и руководству в рамках процессов производства[4];\r\nПолитика — это управление распределением ресурсов;\r\nПолитика — это сфера жизнедеятельности общества, связанная с получением, удержанием и использованием власти;\r\nПолитика — это стремление к участию во власти или оказанию влияния на распределение власти, будь то между государствами, будь то внутри государства между группами людей, которые оно в себе заключает[5];\r\nПолитика — это участие в делах государства, направление государства, определение форм, задач, содержания деятельности государства[6];\r\nПолитика — это деятельность организации (её поведенческая модель), в том числе деятельность государства по реализации своих целей (интересов), например: — техническая политика;\r\nПолитика — всякая программа действий, все виды деятельности по самостоятельному руководству чем или кем либо. Соответственно, в этом значении мы можем говорить, например, о валютной политике банка, о школьной политике городских муниципалитетов, о семейной политике жены в отношении мужа и детей и т. д.;\r\nПолитика — совокупность мер и действий, направленных на достижение заведомо заданного результата;\r\nПолитика — форма общественного сознания, выражающая корпоративные интересы сообщества и проявляющаяся в гражданском обществе (государстве) в виде течений, движений, профсоюзов и других общественных организаций и объединений по специфическим интересам. Наиболее совершенные и организованные из них партии и церковь;\r\nПолитика — это искусство объединения людей;\r\nПолитика — это борьба за право устанавливать свои правила игры;\r\nПолитика — искусство зла во имя добра (философско-этическое определение в широком смысле);\r\nПолитика — приводимый в исполнение навязавшийся указ третьего лица;\r\nПолитика — исполняемая стратегия кого-либо, по предложению прав и свобод. (Политика такая-то, может предлагать права, отличные от прав, предлагаемых другой политикой);\r\nПолитика — меры и действия, предпринимаемые руководящим лицом с целью осуществления идеи о том, как всё должно быть устроено в подвластной ему среде. К примеру, политикой фирмы «А» могут быть изменены какие-нибудь функции в производимом ею оборудовании, с целью увеличения прибыли.', 'политика', '2017-03-29', 1),
	(11, 4, 'Спорт в древности', 'sport-v-drevnosti', 'Развитие и изменение спорта на протяжении человеческой истории может много рассказать как о социальных изменениях в обществе вообще, так и о сущности спорта в частности. Зарождение физической культуры в целом и спорта в частности пытаются объяснить ряд теорий:\r\n\r\n«теории игры», у истоков которых стоял Ф. Шиллер и которые позже развивали Бюхер, Гроос и Летурно, рассматривают всю человеческую культуру, в том числе и физическую, как развитие игровой деятельности. В современной философии наиболее известным сторонником теорий игры является Йохан Хёйзинга[3][4]. К теориям игры примыкает «теория излишней энергии» Г. Спенсера, которая может рассматриваться и как их разновидность[4]; согласно этой теории, первобытный человек, нуждавшийся в высвобождении избыточной энергии, совершенствовал движения (игровые и танцевальные), которые позволяли это наиболее успешно делать[3]\r\n«теория магии» (Рейнак, позже Дим, Кербе, Жиллет) связывает возникновение физической культуры с необходимостью обучения и совершенствования имитирующих труд и охоту плясок и магических ритуалов[4][3]\r\n«теория войны» (Берк) рассматривает физическую культуру и спорт как средство развития физической формы и навыков, необходимых для ведения боя[3]\r\nматериалистическая теория, или «теория труда» (предложенная Г. В. Плехановым и развитая Н. И. Пономарёвым) рассматривает в качестве истоков почти всех современных видов физкультуры и спорта виды трудовой деятельности[4][5]\r\nМногие примеры пещерного искусства, открытые на сегодняшний день, содержат изображения ритуальных церемониальных сцен. Несмотря на то, что действия, запечатлённые на этих изображениях, нельзя отнести исключительно к современному понятию о спорте, всё же можно сделать вывод, что уже тогда существовали занятия и ритуалы, напоминающие спортивную деятельность. Эти изображения, находящиеся во Франции, Африке и Австралии, были сделаны 30 000 лет назад. В Монголии обнаружено датируемое седьмым тысячелетием до н. э. наскальное изображение окружённых толпой борцов[6]. Наскальные рисунки, обнаруженные в Японии, изображают поединки в стиле сумо, по-видимому, связанные с сельскохозяйственными религиозными ритуалами[7].', 'спорт история', '2017-03-29', 1),
	(12, 4, 'Виды спорта', 'vidy-sporta', 'Вид спорта — это совокупность видов спортивных соревнований, объединённых по признакам схожести правил, одной спортивной федерации и т. п. Традиционно с точки зрения Международного олимпийского комитета отдельным видом спорта считается совокупность всех видов соревнований, проводимых одной международной федерацией. Исключение составляли современное пятиборье и биатлон, которыми до 1993 года руководила одна международная федерация.\r\n\r\nВиды спорта, признанные МОК в соответствии с Олимпийской хартией, называются олимпийскими; только олимпийские виды спорта могут быть включены в программу Олимпийских игр. В Олимпийской хартии приводится исчерпывающий список международных спортивных федераций, руководящих видами спорта, признанными олимпийскими; всего в этом списке на 2013 год насчитывается 35 федераций, ограничения связаны с принятием Всемирного антидопингового кодекса, независимостью результатов в первую очередь от «механической движущей силы», а также с распространённостью того или иного вида. Критерии распространённости:\r\n\r\nдля летних Олимпийских игр — не менее чем в 75 странах на 4 континентах для мужского вида и не менее чем в 40 странах на 3 континентах для женского вида\r\nдля зимних Олимпийских игр — не менее чем в 25 странах на 3 континентах[38].\r\nПод эгидой международного конвента «СпортАккорд» собрано значительно большее число международных федераций — 93 обладающих полными членскими правами и 16 ассоциированных[39]). Олимпийская хартия выделяет как особую группу зимние виды спорта (согласно Правилу 6 Олимпийской хартии, «виды спорта, которыми занимаются на снегу или на льду») — только они могут включаться в программу зимних Олимпийских игр.', 'спорт', '2017-03-29', 1),
	(13, 4, 'Спортивные награды', 'sportivnye-nagrady', 'К спортивным призам и наградам, вручаемых победителям и призёрам спортивных соревнований, относятся:\r\n\r\nмедали Олимпийских игр, мировых, континентальных, национальных и прочих чемпионатов. Обычно участнику, занявшему 1-е место, присуждается «золотая медаль», 2-е — «серебряная», 3-е — «бронзовая»;\r\nнаграды за установление рекордов мира, континента или страны;\r\nлавровые венки, ленты чемпионов;\r\nкубки и прочие переходящие призы\r\nдипломы, жетоны и пр.', 'спорт', '2017-03-29', 1),
	(14, 2, 'История изучения', 'istoriya-izucheniya', 'В философии и науке XVIII века противоречивость юмора рассматривалась в русле поиска логики построения юмористического образа. Так, шотландский философ Дж. Битти в эссе о смехе обозначил объект своего труда как «результат наблюдения двух или более нелогичных, несоответствующих или нелепых частей или обстоятельств, рассматривающихся как объединенные в одном сложном объекте или группе»[2]. А. Шопенгауэр в работе «Мир как воля и представление» указывал, что два несовпадающих элемента, из которых возникает смешное - это усвоенные нами понятие и реальный объект, который «мыслился в этом понятии» (но в действительности оказался отличным от него)[3].\r\n\r\nВ теории разрядки юмор трактуется как механизм «освобождения от напряжения», как своеобразный «предохранительный клапан». Шотландский философ XIX века А. Бэн и английский социолог Г. Спенсер также рассматривали смех как разрядку[4]. Элементы теории разрядки можно найти и в подробной интерпретации оснований юмора, предложенной З. Фрейдом. В работе «Остроумие и его отношение к бессознательному» основатель психоанализа четко определяет различие между юмором, комизмом и остротой. З. Фрейд считал, что юмор – это средство решения психической проблемы простейшими способами, успешно опробованными индивидами в самом начале жизни[5]. По мнению Э. Оринга, юмор зависит от восприятия «соответствующего несоответствия» («appropriate incongruity»)[6]. Его ученый понимает как нахождение связи между двумя категориями, которые с позиции здравого смысла рассматривались бы как противоречивые[4].\r\n\r\nАнглийский социолог М. Малкэй пишет, что в юмористическом образе «правила логики, ожидания здравого смысла, научные законы и требования приличий» не являются обязательными. Он полагает, что общественная жизнь включает в себя множество противоречивых значений и смыслов, и юмор с его релятивизмом, создает особую среду, особый «язык» для выражения этих противоречий. Тем самым, юмор служит поддержанию социального порядка[', 'юмор философия история', '2017-03-29', 1),
	(15, 2, 'Этимология', 'etimologiya', 'Слово юмор имеет древнегреческие корни. В античной медицине состояние человека описывалось соотношением четырёх жидкостей — крови, лимфы, жёлтой и чёрной желчи — которые они называли «гумор» (ср.: гуморальный). По мнению древних именно соотношение этих жидкостей определяли здоровье и самочувствие человека.', 'юмор', '2017-03-29', 1),
	(16, 2, 'Отношение религии к юмору', 'otnoshenie-religii-k-yumoru', 'Одно из библейских «посланий» включает такие слова: «также сквернословие и пустословие и смехотворство не приличны вам, а, напротив, благодарение» (Еф. 5:4). Несмотря на это, в Интернете широко распространен «христианский» юмор.\r\nИслам выработал целый ряд правил относительно юмора', 'юмор религия', '2017-03-29', 1),
	(17, 6, 'Test', 'test', '<h2 style="text-align: center;">Test post</h2>\r\n<ul>\r\n<li>litem1</li>\r\n<li>litem2</li>\r\n<li>litem3</li>\r\n</ul>\r\n<p><img src="../../../../files/uploads/yii2-comments.png" width="600" height="555" /></p>', 'test', '2017-03-30', 1);
/*!40000 ALTER TABLE `blog_posts` ENABLE KEYS */;

-- Дамп структуры для таблица aii.content_pages
DROP TABLE IF EXISTS `content_pages`;
CREATE TABLE IF NOT EXISTS `content_pages` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Название',
  `alias` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Алиас',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Опубликовано',
  `h1` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'H1',
  `title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Title',
  `description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Description',
  `keywords` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Keywords',
  `text` text COLLATE utf8_unicode_ci COMMENT 'Текст',
  PRIMARY KEY (`id`),
  UNIQUE KEY `UNIQUE` (`alias`),
  KEY `ALIAS` (`alias`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='Страницы сайта';

-- Дамп данных таблицы aii.content_pages: ~3 rows (приблизительно)
/*!40000 ALTER TABLE `content_pages` DISABLE KEYS */;
INSERT INTO `content_pages` (`id`, `name`, `alias`, `status`, `h1`, `title`, `description`, `keywords`, `text`) VALUES
	(1, '404', 'error', 1, '404', '404', '', '', '<h2 style="text-align: center;">&nbsp;</h2>\r\n<h2 style="text-align: center;">Страница не найдена!</h2>'),
	(2, 'О проекте', 'about', 1, 'О проекте', 'О проекте', 'О проекте', 'Блог, YII2', '<h4><strong>Задание по саморазвитию</strong></h4>\r\n<h5>&nbsp;</h5>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<h5><strong>Общее описание проекта:</strong></h5>\r\n<p>Проект представляет из себя небольшой новостной ресурс по типу &ldquo;Блог&rdquo;. В данном проекте пользователи сайта могут смотреть и комментировать интересующие их посты.</p>\r\n<p>&nbsp;</p>\r\n<h5><strong>Цель:</strong></h5>\r\n<ol>\r\n<li>Закрепить полученные данные в курсе</li>\r\n<li>Научиться работать с framework Yii2 / Symfony 3</li>\r\n<li>Реализовать собственный проект&nbsp;</li>\r\n</ol>\r\n</td>\r\n<td><img src="../../../../files/uploads/yii2-comments.png" width="200" height="185" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h5><strong>Вводные дданые:</strong></h5>\r\n<ol>\r\n<li>Проект должен быть реализован с использованием одного из frameworks Yii2, Symfony 3</li>\r\n<li>Проект должен содержать административную часть для управления сущностями</li>\r\n<li>Дизайн и верстка для проекта допускается произвольная</li>\r\n</ol>\r\n<h5>&nbsp;</h5>\r\n<h5>&nbsp;</h5>\r\n<h5><strong>Структура сайта:</strong></h5>\r\n<p><span style="font-weight: 400;">Структура сайта состоит из следующих страниц: Главная,О проекте, Контакты, Категория, Тег, Пост, Личный кабинет</span></p>\r\n<h5>&nbsp;</h5>\r\n<h5>&nbsp;</h5>\r\n<h5><strong>Компоненты и модули:</strong></h5>\r\n<ul>\r\n<li style="font-weight: 400;"><span style="font-weight: 400;">Административная панель</span></li>\r\n<li style="font-weight: 400;"><span style="font-weight: 400;">Модуль категории</span></li>\r\n<li style="font-weight: 400;"><span style="font-weight: 400;">Модуль поста</span></li>\r\n<li style="font-weight: 400;"><span style="font-weight: 400;">Модуль тегов</span></li>\r\n<li style="font-weight: 400;"><span style="font-weight: 400;">Статичные страницы</span></li>\r\n</ul>\r\n<p>&nbsp;</p>'),
	(3, 'Контакты', 'contact', 1, 'Контакты', 'Контакты', 'Контакты', 'Контакты', '<h3>Сайт расположен:</h3>\r\n<p>DNS: is.esy.es -&nbsp;hostinger.com.ua</p>\r\n<p>VPS: Amazon EC2</p>\r\n<p>GIT:&nbsp;<a title="github.com/akmktz/aii" href="https://github.com/akmktz/aii">github.com/akmktz/aii</a></p>\r\n<h5>&nbsp;</h5>');
